using System.Collections.Generic;
using Nethereum.Hex.HexConvertors.Extensions;
using NUnit.Framework;
using Web3Unity.Scripts.Library.Ethers.RLP;

namespace Tests
{
    public class RLPTests
    {
        [Test]
        [TestCase("0x01", "0x01")]
        [TestCase("0x01010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101", "0xb86401010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101")]
        [TestCase("", "")]
        [TestCase("0x010101010101010101010101010101", "0x8f010101010101010101010101010101")]
        public void EncodesRLPTest(string decoded, string encoded)
        {
            byte[] byteArray2 = HexByteConvertorExtensions.HexToByteArray(decoded);
            byte[] expectedEncoded2 = HexByteConvertorExtensions.HexToByteArray(encoded);
            // Act
            byte[] byteEncoded = RLP.EncodeElement(byteArray2);
            
            // Assert
            Assert.AreEqual(expectedEncoded2, byteEncoded);
        }
        
        [Test]
        public void EncodeElement_String_ReturnsByteArray2()
        {
            List<byte[]> byteArrayList = new List<byte[]>();
            
            byte[] byte1 = HexByteConvertorExtensions.HexToByteArray("0x");
            byte[] byte2 = HexByteConvertorExtensions.HexToByteArray("0x");
            byte[] byte3 = HexByteConvertorExtensions.HexToByteArray("0x");
            
            byteArrayList.Add(byte1);
            byteArrayList.Add(byte2);
            byteArrayList.Add(byte3);
            
            byte[] expectedEncoded2 = HexByteConvertorExtensions.HexToByteArray("0xc3808080");
            
           
            byte[] encoded = RLP.EncodeList(byteArrayList.ToArray());
           

            // Assert
            Assert.AreEqual(expectedEncoded2, encoded);
       

        }
    }
}